SELECT sm.id, sm.parent_id, sm.route_name, TO_CHAR(sm.route_path) as route_path, TO_CHAR(sm.component_path) as component_path, sm.menu_name, sm.leaf_flag, TO_CHAR(sm.icon) as icon, sm.order_num, sm.enable_flag, sm.hidden_flag, sm.cache_flag, sm.create_time, sm.update_time, sm.update_user FROM sys_menu sm LEFT JOIN sys_role_menu srm ON sm.id = srm.menu_id LEFT JOIN sys_role sr ON srm.role_id = sr.id where sm.enable_flag = 1
#{roleCode}
GROUP BY sm.id, sm.parent_id, sm.route_name, TO_CHAR(sm.route_path), TO_CHAR(sm.component_path), sm.menu_name, sm.leaf_flag, TO_CHAR(sm.icon), sm.order_num, sm.enable_flag, sm.hidden_flag, sm.cache_flag, sm.create_time, sm.update_time, sm.update_user ORDER BY sm.order_num

